import React, { useState } from 'react';

// import { TransactionHistory } from './TransactionHistory/TransactionHistory';
import { Statistics } from './Statistics/Statistics';
import { FeedbackOptions } from './FeedbackOptions/FeedbackOptions';
import { Section } from './Section/Section';

import './App.css';

function App() {
  const options = [
    {text: 'Good', id: 1},
    {text: 'Neutral', id: 2},
    {text: 'Bad', id: 3},
  ]

  const [good, setGood] = useState(0)

  const [neutral, setNeutral] = useState(0)

  const [bad, setBad] = useState(0)

  const handleLeaveFeedback = id => {
    if (id === 1) setGood(value => value + 1)
    if (id === 2) setNeutral(value => value + 1)
    if (id === 3) setBad(value => value + 1)
  }

  const totalValue = () => good + neutral + bad

  const positivePercentage = () => {
    const total = good + neutral + bad
    // console.log(good , neutral , bad, good * 100, 0/0);
    const percent = (good * 100) / total
    // console.log(percent);
    return parseFloat(percent.toFixed(1)) || 0
  }

  return (
    <div className='App'>
      {/* <Profile {...user} /> */}
      <Section title='Pleace leave feedback'>
        <FeedbackOptions options={options} onLeaveFeedback={handleLeaveFeedback} />
      </Section>
      <Section title='Statistics'>
        <Statistics good={good} neutral={neutral} bad={bad} total={totalValue()} positivePercentage={positivePercentage()} />
      </Section>
      {good}
      {neutral}
      {bad}
      {/* {/* <FrendList friends={friends} /> */}
      {/* <TransactionHistory items={transactions} /> */}
    </div>
  );
}

export default App;
